mapper.select:
SELECT DISTINCT t2.privilege_id,t3.url,t3.parent_id,t3.name,t3.sort_no,t3.menu_image FROM t_user_role t1 left JOIN t_role_privilege t2 on t2.role_id = t1.role_id left join t_privilege t3 on t3.privilege_id = t2.privilege_id WHERE 1=1 AND t3.menu_flag = '1'
AND t1.user_id = #{userId,jdbcType=VARCHAR}
order BY t3.sort_no ASC
