mapper.select:
SELECT t1.user_id,t1.user_name,t1.identity_no,t1.email,t1.gender,t1.password,t1.mobile,t3.project_code projectCode, t3.project_name projectName, t4.dept_name deptName,t7.company_name companyName,t8.role_name,t9.id userRoleId FROM t_user t1 LEFT JOIN t_user_project t2 on t2.user_id = t1.user_id LEFT JOIN t_project t3 on t3.id = t2.project_id LEFT JOIN t_user_department t6 on t6.user_id = t1.user_id LEFT JOIN t_department t4 on t4.dept_id = t6.dept_id LEFT JOIN t_department_company t5 on t5.dept_id = t4.dept_id LEFT JOIN t_company t7 on t7.company_id = t5.company_id LEFT JOIN t_user_role t9 ON t9.user_id = t1.user_id LEFT JOIN t_role t8 ON t8.role_id = t9.role_id WHERE 1=1
AND t2.project_id = #{projectId,jdbcType=INTEGER}
AND t7.company_id = #{companyId,jdbcType=INTEGER}
AND t8.role_type = #{roleType,jdbcType=VARCHAR}
ORDER BY t1.user_id ASC
